    private void verifica(String t, String d, String g, String p, int a){        
        Filme f = new Filme();
        f.setAno(a);
        if(f.getAno() != 0 && !t.isEmpty() && !d.isEmpty() && !g.equals(" ") && !p.equals(" ")){            
            fila.enqueue(new Filme(t, d, g, p, a));
            JOptionPane.showMessageDialog(null, "Cadastro com sucesso !", "Êxito", JOptionPane.INFORMATION_MESSAGE);
            jTextField1.setText("");
            jTextField2.setText("");
            jComboBox1.setSelectedIndex(0);
            jComboBox2.setSelectedIndex(0);
            jTextField5.setText("");
            jTextField1.requestFocus();            
            p01.setVisible(true);
            p02.setVisible(true);
            p03.setVisible(true);
            p04.setVisible(true);
            p05.setVisible(true);
        }else{
            if (f.getAno() == 0 && t.isEmpty() &&  d.isEmpty() && g.equals(" ") && p.equals(" ")) {
                jTextField5.setText("");
                jTextField5.requestFocus();
            }
            else{
                if(f.getAno() != 0 && t.isEmpty() || d.isEmpty() || g.equals(" ") || p.equals(" ")){
                    JOptionPane.showMessageDialog(null, "Preencha os campos restantes!", "Aviso", JOptionPane.INFORMATION_MESSAGE);
                }else{
                    p05.setVisible(true);
                    jTextField5.setText("");
                    jTextField5.requestFocus();
                }
            }
        }   
    }
    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // Lógica do botão Cadastrar
        String titulo = jTextField1.getText();
        String diretor = jTextField2.getText();
        String genero = jComboBox1.getSelectedItem().toString();
        String country = jComboBox2.getSelectedItem().toString();        
        // bloco try-catch para capturar erros nos dados: 
        try {
            int ano = Integer.parseInt(jTextField5.getText().trim());                     
            try {            
                verifica(titulo,diretor,genero,country, ano);               
            } catch (Exception e) {
               JOptionPane.showMessageDialog(null, "Preencha Todos os campos!", "Aviso", JOptionPane.INFORMATION_MESSAGE);                                    
            }
        } catch (NumberFormatException e) {
            jTextField5.setText("");            
            JOptionPane.showMessageDialog(null, "Preencha Todos os campos!", "Aviso", JOptionPane.INFORMATION_MESSAGE);
        }        
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // Botão Listar filmes cadastrados:
        jTextArea1.setText(fila.toString());                           
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // Botão Retirar até encontrar um filme de ação
        jTextArea1.setText(fila.toString());
        if(fila.isEmpty()){
            JOptionPane.showMessageDialog(null, "Não há filmes para remover!", "Aviso", JOptionPane.INFORMATION_MESSAGE);
        }else{
            boolean temAcao = false;
            while(!fila.isEmpty()){                
                String guarda = fila.peek()+"";
                if(guarda.split(",")[2].contains("Ação") == false){                
                    fila.dequeue();                
                }else{
                    temAcao = true;
                    break;
                } 
            }
            if(temAcao == false){
                JOptionPane.showMessageDialog(null, "Não foi encontrado nenhum filme de ação!", "Aviso", JOptionPane.INFORMATION_MESSAGE);
                jTextArea1.setText(fila.toString());
            }
            else{
                JOptionPane.showMessageDialog(null, "Não há mais filmes para remover!\nFilme(s) de ação encontrado(s)!", "Aviso", JOptionPane.INFORMATION_MESSAGE);
                jTextArea1.setText(fila.toString());
            }  
        } 
    }//GEN-LAST:event_jButton3ActionPerformed
        
